        </div>
        <div class="discount-header-content" v-else>
          <span>{{ $t('您已晋升至最高级, 可享有： 洗码利率') }}</span>
          <b>{{ getVipsConfig[viplevel].bounsRate }} %</b>
          <span class="discount-header-content-right">{{ $t('晋级礼金') }}</span>
