          userInfo.nowMemberVip ? userInfo.nowMemberVip.vipShow : ""
        }}</span>
      </div>
    </div>
    <!-- 优惠列表 -->
    <div class="myTitle3">{{ $t('自助优惠列表') }}</div>
    <ul class="dislist el-row">
      <li style="display: flex" v-for="(item, i) of list" :key="i" @click="toPage(item)">
        <template v-if="item.mPicture">
          <div>
            <img loading="lazy" v-lazy="$common.getImgUrl(item.mPicture)" />
          </div>
        </template>
        <template v-else>
          <div>
            <img loading="lazy" v-if="item.type == 99" v-lazy="'data:image/png;base64,'
              " />
            <img loading="lazy" v-if="item.type == 4" v-lazy="'data:image/png;base64,'
              " />
            <img loading="lazy" v-if="item.type == 5" v-lazy="'data:image/png;base64,'
              " />
            <img loading="lazy" v-if="item.type == 6" v-lazy="'data:image/png;base64,'
              " />
            <img loading="lazy" v-if="item.type == 7" v-lazy="'data:image/png;base64,'
              " />
