              " />
            <img loading="lazy" v-if="item.type == 9" v-lazy="'data:image/png;base64,'
              " />
            <img loading="lazy" v-if="item.type == 10" v-lazy="'data:image/png;base64,'
              " />
            <img loading="lazy" v-if="item.type == 11" v-lazy="'data:image/png;base64,'
              " />
