<script>
import { mapGetters } from "vuex";
export default {
  data() {
    return {
      list: [],
    };
  },
  computed: {
    ...mapGetters(["userInfo", "getVipsConfig"]),
    viplevel: function () {
      if (this.userInfo && this.userInfo.nowMemberVip) {
        return this.userInfo.nowMemberVip.vipLevel || 0;
      }
      return 0;
    },
  },
  created() {
    console.log(this.userInfo, "userInfo");
    let data = {
      pageSize: 40,
    };
    this.getVipsInfo();
    this.$http
      .post(this.$api.getThematicActivitiesListByApp, data)
      .then((res) => {
        if (res.code == 0) {
          this.list = res.data.content.filter(item => { return (!item.name.includes(this.$t('砸蛋'))
           && !item.name.includes(this.$t('水球')) && !item.name.includes(this.$t('刮刮乐')) && !item.name.includes(this.$t('盲盒'))) && !item.name.includes(this.$t('签到')) });
          this.list.splice(1, 0, {
            // 插入第二个
            name: "洗码积分",
            summary: "最高返水1.3%",
            type: 99,
            id: 1,
          });
        }
      });
  },
  methods: {
    // 获取vip等级信息
    getVipsInfo() {
      this.$http.post(this.$api.memberVipConfig).then((res) => {
        if (res.code) return;
        this.$store.commit("setVipsConfig", res.data);
      });
    },
    // 跳转双旦活动
    // 跳转双旦活动
    jump11(item) {
      const obj = {
        activityId: item.id,
        token: this.$common.getToken(),
        clientCode: window.clientCode,
        username: this.$common.getUser().username,
        language: this.$i18n.locale,
        theme: window.theme,
        host: this.$config.baseUrl,
      };
      const str = window.btoa(JSON.stringify(obj));
      const url =
        window.location.origin +
        "/func/" +
        item.thematicActivitiesFolder +
        "/pc/index.html?s=" +
        str;
      window.open(url);
    },
    toPage(value) {
      document.body.scrollTop = document.documentElement.scrollTop = 0;
      switch (value.type) {
        case 99:
          this.$router.push({
            path: "/my/discount/codeWash",
          });
          break;
        case 3: // 双旦活动
          this.jump11(value);
          break;
        case 4:
          this.$router.push({
            path: "/my/discount/credentials",
            query: {
              did: value.id,
            },
          });
          break;
        case 5:
          this.$router.push({
            path: "/my/discount/week",
            query: {
              did: value.id,
            },
          });
          break;
        case 6:
          this.$router.push({
            path: "/my/discount/lucky",
            query: {
              did: value.id,
            },
          });
          break;
        case 7:
          this.$router.push({
            path: "/my/discount/anniversary",
            query: {
              did: value.id,
            },
          });
          break;
        case 8:
          this.$router.push({
            path: "/my/discount/Reward",
            query: {
              did: value.id,
              name: value.name,
            },
          });
          break;
        case 9:
          this.$router.push({
            path: "/my/discount/Rescue",
            query: {
              did: value.id,
              name: value.name,
            },
          });
          break;
        case 10:
          this.$router.push({
            path: "/my/discount/manyTimes",
            query: {
              did: value.id,
            },
          });
          break;
        case 11:
          this.$router.push({
            path: "/my/discount/feedback",
            query: {
              did: value.id,
            },
          });
          break;
      }
    },
  },
};
</script>
